import React from 'react';
import './Navigation.css';

const Navigation = (props) => {

    function ani(){
        if (document.getElementById("navi-toggle").checked == false) {

            document.getElementById("we-text").style.visibility = "visible";
            document.getElementById("are-text").style.visibility = "visible";
            document.getElementById("about-text").style.visibility = "visible";
            document.getElementById("experiences-text").style.visibility = "visible";

            /* White text */
            document.getElementById("navmain").style.visibility = "visible";
            document.getElementById("navmain").style.width = "97%";
            document.getElementById("navmain").style.opacity = "1";
            } 
        else {

            document.getElementById("we-text").style.visibility = "hidden";
            document.getElementById("are-text").style.visibility = "hidden";
            document.getElementById("about-text").style.visibility = "hidden";
            document.getElementById("experiences-text").style.visibility = "hidden";

            /* White text */
            document.getElementById("navmain").style.visibility = "hidden";
            document.getElementById("navmain").style.width = "0";
            document.getElementById("navmain").style.opacity = "0";
            }
    }

        return (
            <div className="navigation__fullscreen">
                <input type="checkbox" checked={props.checked} onChange={props.handleManageCheckBox} onClick={ani} className="navigation__checkbox" id="navi-toggle"/>
                <label htmlFor="navi-toggle" className="navigation__button">
                    <span className="navigation__icon">&nbsp;</span>
                </label>
                <div className="navigation__background">&nbsp;</div>  

                {/* NAVIGATION */}

                <div className="navmain" id="navmain">
                    <div className="navmain-we"> 
                        <span> W </span>
                        <span> E </span> 
                    </div>

                    <div className="navmain-line"></div>

                    <div className="navmain-are"> 
                        <span>A</span>
                        <span>R</span>
                        <span>E</span>
                    </div>

                    <div className="navmain-about" id="navabout-text"> 

                    </div>

                    <div className="navmain-experiences" id="navexperiences-text">

                    </div>
                </div>
            </div>
        );
}

export default Navigation;
